document.addEventListener('DOMContentLoaded',function(){const hamburger=document.querySelector('.hamburger');const navMenu=document.querySelector('.nav-menu');if(hamburger&&navMenu){hamburger.addEventListener('click',function(){hamburger.classList.toggle('active');navMenu.classList.toggle('active');});const navLinks=document.querySelectorAll('.nav-link');navLinks.forEach(link=>{link.addEventListener('click',function(){hamburger.classList.remove('active');navMenu.classList.remove('active');});});document.addEventListener('click',function(event){const isClickInsideNav=navMenu.contains(event.target);const isClickOnHamburger=hamburger.contains(event.target);if(!isClickInsideNav&&!isClickOnHamburger&&navMenu.classList.contains('active')){hamburger.classList.remove('active');navMenu.classList.remove('active');}});}const currentLocation=window.location.pathname;const navLinks=document.querySelectorAll('.nav-link');navLinks.forEach(link=>{if(link.getAttribute('href')===currentLocation.split('/').pop()||(currentLocation.endsWith('/')&&link.getAttribute('href')==='index.html')){link.classList.add('active');}});});